chunk.buffer:

p returns [String val]
	:	^(P_START type=P_ATTR_TYPE?)
	{
		if($type != null)
		{
			String pt = $type.text;

			if(pt.equals("stress"))
				$val = "/";
			else if(pt.equals("accented nucleus"))
				$val = "//";
			else if(pt.equals("contrastive stress"))
				$val = "///";
			else if(pt.equals("drawl"))
				$val = ":";
			else if(pt.equals("pause"))
				$val = "^";
			else if(pt.equals("blocking"))
				$val = "^";
		}
	}
	;
