chunk.buffer:

pause returns [String val]
	:	^(PAUSE_START sl=PAUSE_ATTR_SYMBOLIC_LENGTH? len=PAUSE_ATTR_LENGTH?)
	{
		$val = "(pause:";
		if($len != null) {
		    $val = "(pause:" + $len.text + ")";
		} else {
		    String t = $sl.text;
		    if(t.equals("simple"))  {
		        $val = "(.)";
		    } else if(t.equals("long")) {
		        $val = "(..)";
		    } else if(t.equals("very long")) {
		        $val = "(...)";
		    }
		}
	}
	;
