chunk.buffer:

replacement returns [String val]
scope {
    String buffer;
}
@init {
    $replacement::buffer = "";
}
@after {
    $val = "(replacement:" + $replacement::buffer.toString() + ")";
}
	:	^(REPLACEMENT_START replacementele*)
	;

replacementele
    :    w
    {
        $replacement::buffer += ($replacement::buffer.length() > 0 ? " " : "") + $w.val;
    }
    ;
