chunk.buffer:

ss returns [String val]
	:	^(SS_START type=SS_ATTR_TYPE)
	{
			if($type != null)
			{
				if($type.text.equals("1"))
					$val = SyllableStress.PrimaryStress.getIpa() + "";
				else if($type.text.equals("2"))
					$val = SyllableStress.SecondaryStress.getIpa() + "";
			} else {
				$val = "";
			}
	}
	;
