chunk.buffer:

pg
scope {
	boolean inPg;
}
@init {
	$pg::inPg = true;
}
@after {
	$pg::inPg = false;
}
	:	PG_START ( pgele )* ( pho )* align? PG_END
	->	^(PG_START pgele* pho* align?)
	;

pgele
	:	w
	|	g
	|	pause
	|  	e
	|	underline
	|	overlap
	|	tagmarker
	|	r 
	|	k
	|	ga
	|	s
	|	error
	;


pg returns [String val]
scope {
	String buffer;
	int phoCount;
}
@init {
	$pg::buffer = new String();
	if($g.size() > 0) {
		$g::pgCount++;
	}
}
@after {
	$val = $pg::buffer.toString();
}
	:	^(PG_START pgele* pho* align?)
	;

pgele returns [String val]
@init {
	$val = new String();
}
@after {
	$pg::buffer += ($pg::buffer.length() > 0 ? " " : "") + $val;
}
	:	w 
	{
		$val = $w.val;
	}
	|	g
	{
		$val = "{" + $g.val + "}";
	}
	|	pause
	{
		$val = $pause.val;
	}
	|  	e
	{
		$val = $e.val;
	}
	|	underline
	{
		$val = $underline.val;
	}
	|	overlap
	{
		$val = "(" + $overlap.val + ")";
	}
	|	tagmarker
	{
		$val = $tagmarker.val;
	}
	|	r 
	{
		$val = "(x" + $r.val + ")";
	}
	|	k
	{
		$val = "(" +  $k.val + ")";
	}
	|	ga
	{
		$val = $ga.val;
	}
	|	s
	{
		$val = $s.val;
	}
	|	error
	{
		$val = $error.val;
	}
	;
